let updates = document.querySelectorAll(".update");
const myModal = document.querySelector(".mymodal");
const times = document.querySelector(".times");
let myModalContent = myModal.querySelector(".mymodal-content");

updates = Array.from(updates);

times.addEventListener("click", function () {
    myModal.classList.add("hide");
})
updates.forEach(function (item, i , updates) {
    item.addEventListener("click", function () {
        displayModal(item.nextSibling);
    });

});
console.log(myModalContent);
console.log(times);

function displayModal(list){
    console.log(list);
    let listOL = String(list);
    myModalContent.appendChild(list);
    // myModalContent.innerHTML = listOL;
    myModal.classList.toggle("hide");
}
