
let updates = document.querySelectorAll(".update");
const myModal = document.querySelector(".mymodal");
let myModalContent = myModal.querySelector(".mymodal-content__text");
const times = document.querySelector(".times");
let olContent = "test";
let index = 0;
times.addEventListener("click", closeModal);

updates.forEach(function (item, i , updates) {
    item.addEventListener("click", function () {
        displayModal(item, i);
    });
});
myModal.addEventListener("click", function (e) {
    if(e.target.classList.contains("mymodal")) closeModal();
})
function closeModal(){
    let update__toggle;
    myModal.classList.add("hide");
    update__toggle = updates[index].querySelector(".update__content");
    update__toggle.appendChild(olContent);
    update__toggle.classList.add("hide");
}
function displayModal(list, i){
    index = i;
    olContent = list.querySelector(".update__content ol");
    myModalContent.appendChild(olContent);
    olContent.classList.remove("hide");
    myModal.classList.remove("hide");
}
